mapper.select:
select user_no as userNo, email, password, name, gender, birth, phone
from user
where email=#{email} and password=#{password}

select user_no as userNo, email, password, name, gender, birth, phone
from user
where email=#{email}

mapper.update:
update user
set password=#{password}, name=#{name}, gender=#{gender}, birth=#{birth}, phone=#{phone}
where user_no=#{userNo}
