mapper.select:
select notice_no, notice_id, notice_title, notice_content, notice_date, notice_time
  from notice 
 where notice_no = #{notice_no}

mapper.update:
update notice 
   set notice_id = #{notice_id}, 
   	   notice_title = #{notice_title}, 
   	   notice_content = #{notice_content}
 where notice_no = #{notice_no}
